    font-weight:bold;
    font-size:2rem;
    letter-spacing:2px;
    width:100%;
`

    const GalleryParOne = styled.p`
      color:white;
      text-align:center;
      font-size:1rem;
      letter-spacing:2px;
      width:100%;
`

    const GalleryTwo = styled.div`
      display:flex;
